import React, { useState } from 'react';

function AthenaQuery() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const runQuery = async () => {
    try {
      setLoading(true);
      setError(null);
      setResults([]);

      const response = await fetch('http://localhost:3001/api/query-athena', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ query })
      });

      const data = await response.json();

      if (response.ok && data.Rows && data.Rows.length > 0) {
        // Convert Rows to a 2D array
        const rows = data.Rows.map(row =>
          row.Data.map(cell => (cell.VarCharValue !== undefined ? cell.VarCharValue : ''))
        );
        setResults(rows);
      } else {
        setError(data.error || 'No results returned');
      }
    } catch (err) {
      setError('Error: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="page-container">
      <h1>Athena Query Interface</h1>
      <textarea
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Enter your Athena SQL query"
        rows={5}
        cols={80}
      />
      <br />
      <button onClick={runQuery} disabled={loading}>
        {loading ? 'Running...' : 'Run Query'}
      </button>

      {error && <p className="error-message">Error: {error}</p>}

      {results.length > 0 && (
        <table border="1" cellPadding="8" style={{ marginTop: '20px' }}>
          <thead>
            <tr>
              {results[0].map((header, idx) => (
                <th key={idx}>{header}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {results.slice(1).map((row, rowIndex) => (
              <tr key={rowIndex}>
                {row.map((cell, colIndex) => (
                  <td key={colIndex}>{cell}</td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default AthenaQuery;
