import React, { useState, useEffect } from 'react';
import { Search, Mail, RefreshCw, Database, Tool, ChevronRight, X, Menu } from 'lucide-react';

const App = () => {
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [activeSlide, setActiveSlide] = useState(0);
  const [isLoading, setIsLoading] = useState(true);

  // Featured devices data
  const featuredDevices = [
    {
      id: 1,
      name: "VITASCAN PRO",
      category: "Diagnostic",
      description: "Advanced imaging solution with AI-powered diagnostics",
      maintenance: "Quarterly calibration required"
    },
    {
      id: 2,
      name: "PULSEWAVE X3",
      category: "Monitoring",
      description: "Next-gen patient monitoring system with wireless connectivity",
      maintenance: "Monthly software updates recommended"
    },
    {
      id: 3,
      name: "SURGEPRECISION",
      category: "Surgical",
      description: "High-precision surgical device with real-time feedback",
      maintenance: "Sterilization after each use, annual overhaul"
    }
  ];

  // Equipment categories
  const categories = [
    { name: "Diagnostic Equipment", count: 24 },
    { name: "Monitoring Devices", count: 18 },
    { name: "Surgical Tools", count: 36 },
    { name: "Laboratory Equipment", count: 42 },
    { name: "Rehabilitation Devices", count: 15 }
  ];

  // Simulate loading state
  useEffect(() => {
    const timer = setTimeout(() => {
      setIsLoading(false);
    }, 1500);

    return () => clearTimeout(timer);
  }, []);

  // Auto-slide the featured devices
  useEffect(() => {
    const interval = setInterval(() => {
      setActiveSlide((prev) => (prev + 1) % featuredDevices.length);
    }, 5000);

    return () => clearInterval(interval);
  }, [featuredDevices.length]);

  if (isLoading) {
    return (
      <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
        <div className="w-24 h-24 border-8 border-gray-300 border-t-blue-600 rounded-full animate-spin"></div>
        <h2 className="mt-6 text-2xl font-bold text-gray-800">MEDTECH SOLUTIONS</h2>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-white">
      {/* Navigation Bar */}
      <nav className="bg-gray-900 text-white py-4 px-6 sticky top-0 z-50 shadow-md">
        <div className="max-w-7xl mx-auto flex justify-between items-center">
          <div className="flex items-center">
            <span className="font-bold text-2xl mr-10">MEDTECH</span>
            <div className="hidden md:flex space-x-8">
              <NavLink icon={<Database size={18} />} text="Data Generation" />
              <NavLink icon={<Search size={18} />} text="Records Search" />
              <NavLink icon={<Tool size={18} />} text="Maintenance" />
              <NavLink icon={<RefreshCw size={18} />} text="Updates" />
              <NavLink icon={<Mail size={18} />} text="Mail Updation" />
            </div>
          </div>

          <div className="flex items-center">
            <button className="bg-white text-gray-900 px-4 py-2 rounded-full font-medium hover:bg-gray-200 transition duration-300 hidden md:block">
              Login
            </button>
            <button 
              className="ml-4 md:hidden" 
              onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
            >
              {mobileMenuOpen ? <X size={24} /> : <Menu size={24} />}
            </button>
          </div>
        </div>

        {/* Mobile Menu */}
        {mobileMenuOpen && (
          <div className="md:hidden py-4 px-6 bg-gray-800 animate-slideDown">
            <div className="flex flex-col space-y-4">
              <MobileNavLink icon={<Database size={18} />} text="Data Generation" />
              <MobileNavLink icon={<Search size={18} />} text="Records Search" />
              <MobileNavLink icon={<Tool size={18} />} text="Maintenance" />
              <MobileNavLink icon={<RefreshCw size={18} />} text="Updates" />
              <MobileNavLink icon={<Mail size={18} />} text="Mail Updation" />
              <button className="bg-white text-gray-900 px-4 py-2 rounded-full font-medium hover:bg-gray-200 transition duration-300 w-full mt-4">
                Login
              </button>
            </div>
          </div>
        )}
      </nav>

      {/* Hero Section */}
      <div className="bg-gradient-to-r from-blue-100 to-blue-50 py-20 px-6">
        <div className="max-w-7xl mx-auto">
          <div className="flex flex-col md:flex-row items-center">
            <div className="md:w-1/2 animate-fadeIn">
              <h1 className="text-5xl md:text-6xl font-bold text-gray-900 leading-tight">
                PRECISION MEDICAL DEVICES
              </h1>
              <p className="text-xl text-gray-700 mt-4 mb-8">
                Advanced technology with reliability and performance for modern healthcare
              </p>
              <div className="flex space-x-4">
                <button className="bg-blue-600 text-white px-8 py-3 rounded-full font-medium hover:bg-blue-700 transition duration-300">
                  View Devices
                </button>
                <button className="border border-gray-900 text-gray-900 px-8 py-3 rounded-full font-medium hover:bg-gray-100 transition duration-300">
                  Maintenance Guide
                </button>
              </div>
            </div>
            <div className="md:w-1/2 mt-10 md:mt-0 animate-slideInRight">
              <div className="bg-white rounded-lg shadow-xl p-6 transform rotate-2 hover:rotate-0 transition-transform duration-300">
                <div className="text-sm font-semibold text-blue-600 mb-2">FEATURED DEVICE</div>
                <h2 className="text-3xl font-bold">{featuredDevices[activeSlide].name}</h2>
                <div className="inline-block px-3 py-1 bg-gray-100 rounded-full text-sm font-medium text-gray-800 mt-2">
                  {featuredDevices[activeSlide].category}
                </div>
                <p className="mt-4 text-gray-600">{featuredDevices[activeSlide].description}</p>
                <div className="mt-6 flex items-center text-blue-600">
                  <Tool size={16} className="mr-2" />
                  <span className="text-sm">{featuredDevices[activeSlide].maintenance}</span>
                </div>
              </div>

              {/* Dots for indicating which slide is active */}
              <div className="flex justify-center mt-6 space-x-2">
                {featuredDevices.map((_, index) => (
                  <button 
                    key={index} 
                    className={`w-3 h-3 rounded-full ${index === activeSlide ? 'bg-blue-600' : 'bg-gray-300'}`}
                    onClick={() => setActiveSlide(index)}
                  />
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Categories Section */}
      <div className="py-16 px-6 bg-white">
        <div className="max-w-7xl mx-auto">
          <h2 className="text-3xl font-bold text-gray-900 mb-8">MEDICAL DEVICE CATEGORIES</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {categories.map((category, index) => (
              <div 
                key={index} 
                className="border border-gray-200 p-6 rounded-lg hover:shadow-lg transition duration-300 hover:border-blue-500 group"
              >
                <div className="flex justify-between items-center">
                  <h3 className="text-xl font-semibold text-gray-900 group-hover:text-blue-600 transition duration-300">
                    {category.name}
                  </h3>
                  <span className="text-sm font-medium text-gray-500">{category.count} Devices</span>
                </div>
                <div className="flex justify-between items-center mt-4">
                  <button className="text-blue-600 font-medium flex items-center hover:text-blue-800 transition duration-300">
                    View All <ChevronRight size={16} className="ml-1" />
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Maintenance Hub Section */}
      <div className="py-16 px-6 bg-gray-50">
        <div className="max-w-7xl mx-auto">
          <div className="flex flex-col md:flex-row items-center">
            <div className="md:w-1/2">
              <h2 className="text-3xl font-bold text-gray-900 mb-4">MAINTENANCE HUB</h2>
              <p className="text-gray-600 mb-6">
                Comprehensive resources for maintaining optimal device performance and longevity.
              </p>
              <div className="space-y-4">
                <MaintenanceFeature 
                  title="Preventive Maintenance Schedules" 
                  description="Automated scheduling for all your devices"
                />
                <MaintenanceFeature 
                  title="Service History Tracking" 
                  description="Complete maintenance logs and analytics"
                />
                <MaintenanceFeature 
                  title="Parts Replacement Guide" 
                  description="Interactive diagrams and ordering system"
                />
                <MaintenanceFeature 
                  title="Technical Support" 
                  description="Direct access to certified technicians"
                />
                <button className="mt-4 bg-blue-600 text-white px-6 py-3 rounded-full font-medium hover:bg-blue-700 transition duration-300">
                  Access Maintenance Hub
                </button>
              </div>
            </div>
            <div className="md:w-1/2 mt-10 md:mt-0 flex justify-center">
              <div className="w-full max-w-md bg-white rounded-lg shadow-xl p-8 animate-float">
                <h3 className="text-xl font-bold mb-4">Maintenance Calendar</h3>
                <div className="space-y-4">
                  <CalendarItem 
                    device="VITASCAN PRO" 
                    date="April 15, 2025" 
                    type="Calibration"
                    status="upcoming"
                  />
                  <CalendarItem 
                    device="PULSEWAVE X3" 
                    date="April 18, 2025" 
                    type="Software Update"
                    status="upcoming"
                  />
                  <CalendarItem 
                    device="SURGEPRECISION" 
                    date="April 10, 2025" 
                    type="Sterilization Check"
                    status="completed"
                  />
                  <CalendarItem 
                    device="LAB ANALYZER PRO" 
                    date="April 22, 2025" 
                    type="Full Inspection"
                    status="upcoming"
                  />
                </div>
