import React, { useState } from 'react';

function AthenaQuery() {
  const [searchTerm, setSearchTerm] = useState('');
  const [results, setResults] = useState([]);
  const [columns, setColumns] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const runSearch = async () => {
    if (!searchTerm.trim()) return;

    try {
      setLoading(true);
      setError(null);
      setResults([]); // Clear results before search
      setColumns([]); // Clear column headers before search

      const response = await fetch('http://localhost:3001/api/search-athena', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ keyword: searchTerm })
      });

      const data = await response.json();

      // Check if the response is successful and contains data
      if (response.ok && Array.isArray(data.data) && data.data.length > 0) {
        const columnNames = Object.keys(data.data[0]);
        const rows = data.data.map(row => columnNames.map(col => row[col] || ''));

        setColumns(columnNames);
        setResults(rows);
      } else {
        setError(data.error || 'No results found.');
      }
    } catch (err) {
      setError('Error: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="page-container" style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1>Search MRI Logs (Athena)</h1>
      <div style={{ marginBottom: '20px' }}>
        <input
          type="text"
          placeholder="Search by status, machine ID, error code..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ width: '400px', padding: '8px', marginRight: '10px' }}
        />
        <button onClick={runSearch} disabled={loading} style={{ padding: '8px 12px' }}>
          {loading ? 'Searching...' : 'Search'}
        </button>
      </div>

      {error && (
        <p className="error-message" style={{ color: 'red', marginTop: '15px' }}>
          {error}
        </p>
      )}

      {results.length > 0 ? (
        <table border="1" cellPadding="8" style={{ marginTop: '20px', width: '100%', borderCollapse: 'collapse' }}>
          <thead style={{ backgroundColor: '#f0f0f0' }}>
            <tr>
              {columns.map((col, idx) => (
                <th key={idx} style={{ padding: '10px', textAlign: 'left' }}>
                  {col}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {results.map((row, i) => (
              <tr key={i}>
                {row.map((cell, j) => (
                  <td key={j} style={{ padding: '8px' }}>
                    {cell}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p style={{ marginTop: '20px', fontStyle: 'italic' }}>No results found.</p>
      )}
    </div>
  );
}

export default AthenaQuery;
