import React, { useState } from 'react';
import { Container, Typography, Button, Box, CircularProgress, Alert } from '@mui/material';
import EmailIcon from '@mui/icons-material/Email';

function SendEmail() {
  const [status, setStatus] = useState('');
  const [loading, setLoading] = useState(false);
  const API_URL = 'http://localhost:3001';

  const handleSendEmail = async () => {
    setLoading(true);
    setStatus('');
    try {
      const response = await fetch(`${API_URL}/api/send-maintenance-email`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          toEmail: 'sb7762@srmist.edu.in' // ✅ Replace with your verified recipient email
        })
      });

      const result = await response.json();
      if (result.success) {
        setStatus('✅ Email sent to maintenance team!');
      } else {
        setStatus('❌ Failed to send email');
      }
    } catch (err) {
      console.error(err);
      setStatus('❌ Error occurred while sending email');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container maxWidth="sm" sx={{ textAlign: 'center', mt: 6 }}>
      <Typography variant="h4" gutterBottom>
        Send Maintenance Alert
      </Typography>
      <Typography variant="body1" gutterBottom>
        Use this feature to quickly notify the maintenance team when a device needs attention.
      </Typography>

      <Box sx={{ mt: 4 }}>
        <Button
          variant="contained"
          color="secondary"
          startIcon={<EmailIcon />}
          onClick={handleSendEmail}
          disabled={loading}
        >
          {loading ? 'Sending...' : 'Notify Maintenance Team'}
        </Button>
      </Box>

      <Typography variant="caption" color="text.secondary" sx={{ mt: 2, display: 'block' }}>
        An automated email will be sent to the maintenance department for scheduling checks and repairs.
      </Typography>

      <Box sx={{ mt: 3 }}>
        {loading && <CircularProgress />}
        {status && (
          <Alert severity={status.startsWith('✅') ? 'success' : 'error'} sx={{ mt: 2 }}>
            {status}
          </Alert>
        )}
      </Box>
    </Container>
  );
}

export default SendEmail;
