import React, { useState, useEffect } from 'react';

function S3Contents() {
  const [s3Contents, setS3Contents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const API_URL = 'http://localhost:5000';

  useEffect(() => {
    fetchS3Data();
  }, []);

  const fetchS3Data = async () => {
    try {
      setLoading(true);
      const s3Response = await fetch(`${API_URL}/api/s3-contents`);
      if (!s3Response.ok) throw new Error('Failed to fetch S3 contents');
      const s3Data = await s3Response.json();
      setS3Contents(s3Data);
    } catch (error) {
      console.error('Error:', error);
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="page-container">
      <h1>S3 Bucket Contents</h1>
      {error && <div className="error-message">Error: {error}</div>}
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div className="content-section">
          <ul>
            {s3Contents.length === 0 ? (
              <li>No files found in S3 bucket</li>
            ) : (
              s3Contents.map((item, index) => (
                <li key={index}>{item.Key}</li>
              ))
            )}
          </ul>
        </div>
      )}
    </div>
  );
}

export default S3Contents;
