import React, { useState } from 'react';

function SendEmail() {
  const [status, setStatus] = useState('');
  const API_URL = 'http://localhost:3001';

  const handleSendEmail = async () => {
    setStatus('Sending...');
    try {
        const response = await fetch(`${API_URL}/api/send-maintenance-email`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
              toEmail: 'sb7762@srmist.edu.in' // ✅ Replace with your verified recipient email
            })
          });


      const result = await response.json();
      if (result.success) {
        setStatus('✅ Email sent to maintenance team!');
      } else {
        setStatus('❌ Failed to send email');
      }
    } catch (err) {
      console.error(err);
      setStatus('❌ Error occurred');
    }
  };

  return (
    <div className="page-container">
      <h1>Send Maintenance Alert</h1>
      <button onClick={handleSendEmail}>Notify Maintenance Team</button>
      {status && <p>{status}</p>}
    </div>
  );
}

export default SendEmail;
