import React, { useState, useEffect } from 'react';
import {
  BarChart, Bar, PieChart, Pie,
  XAxis, YAxis, CartesianGrid, Tooltip, Legend,
  ResponsiveContainer, Cell
} from 'recharts';


// Custom colors for charts
const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884d8', '#82ca9d'];

// Sample fallback data
const sampleErrorCodeData = [
  { errorCode: "E123", count: 42 },
  { errorCode: "E045", count: 38 },
  { errorCode: "E211", count: 27 },
  { errorCode: "E078", count: 19 },
  { errorCode: "E156", count: 15 }
];


const sampleMachineRuntime = [
  { machineId: "MRI_001", avgRuntime: 452 },
  { machineId: "MRI_002", avgRuntime: 387 },
  { machineId: "MRI_003", avgRuntime: 521 },
  { machineId: "MRI_004", avgRuntime: 312 }
];

export default function Visualization() {
  const [errorCodeData, setErrorCodeData] = useState(sampleErrorCodeData);
  const [machineRuntimeData, setMachineRuntimeData] = useState(sampleMachineRuntime);
  const [loading, setLoading] = useState(true);
  const [dataStatus, setDataStatus] = useState({
    errorCodes: 'fallback',
    predictions: 'fallback',
    runtime: 'fallback'
  });

  useEffect(() => {
    const loadAllData = async () => {
      setLoading(true);
      await Promise.all([
        fetchErrorCodeData(),
        fetchMachineRuntimeData()
      ]);
      setLoading(false);
    };

    loadAllData();

    // Cleanup function for useEffect
    return () => {
      setLoading(false);  // Ensure loading state is reset if component unmounts
    };
  }, []);

  const fetchErrorCodeData = async () => {
    try {
      const response = await fetch('/api/search-athena', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ keyword: 'Error' })
      });

      if (!response.ok) throw new Error('Failed to fetch error codes');
      const result = await response.json();

      if (result.data && result.data.length > 1) {
        const errorCodeCounts = {};

        result.data.slice(1).forEach(row => {
          if (row.Data && row.Data[2] && row.Data[2].VarCharValue) {
            const errorCode = row.Data[2].VarCharValue;
            errorCodeCounts[errorCode] = (errorCodeCounts[errorCode] || 0) + 1;
          }
        });

        const processedData = Object.entries(errorCodeCounts)
          .map(([code, count]) => ({ errorCode: code, count }))
          .sort((a, b) => b.count - a.count)
          .slice(0, 10);

        if (processedData.length > 0) {
          setErrorCodeData(processedData);
          setDataStatus(prev => ({ ...prev, errorCodes: 'loaded' }));
        }
      }
    } catch (error) {
      console.error("Error fetching error codes:", error);
      setDataStatus(prev => ({ ...prev, errorCodes: 'error' }));
    }
  };

  const fetchMachineRuntimeData = async () => {
    try {
      const response = await fetch('/api/latest-s3-log');
      if (!response.ok) throw new Error('Failed to fetch runtime data');
      const result = await response.json();

      if (result.data && result.data.length > 0) {
        const machineRuntimes = {};
        const runtimeCounts = {};

        result.data.forEach(item => {
          if (item.machine_id && item.machine_runtime) {
            const machineId = item.machine_id;
            const runtime = parseInt(item.machine_runtime, 10);

            if (!isNaN(runtime)) {
              if (!machineRuntimes[machineId]) {
                machineRuntimes[machineId] = 0;
                runtimeCounts[machineId] = 0;
              }
              machineRuntimes[machineId] += runtime;
              runtimeCounts[machineId]++;
            }
          }
        });

        const processedData = Object.entries(machineRuntimes)
          .map(([machine, totalRuntime]) => ({
            machineId: machine,
            avgRuntime: Math.round(totalRuntime / (runtimeCounts[machine] || 1))
          }));

        if (processedData.length > 0) {
          setMachineRuntimeData(processedData);
          setDataStatus(prev => ({ ...prev, runtime: 'loaded' }));
        }
      }
    } catch (error) {
      console.error("Error fetching runtime data:", error);
      setDataStatus(prev => ({ ...prev, runtime: 'error' }));
    }
  };

  const refreshData = () => {
    setLoading(true);
    setErrorCodeData(sampleErrorCodeData);
    setMachineRuntimeData(sampleMachineRuntime);
    setDataStatus({
      errorCodes: 'loading',
      runtime: 'loading'
    });

    setTimeout(() => {
      Promise.all([
        fetchErrorCodeData(),
        fetchMachineRuntimeData()
      ]).then(() => {
        setLoading(false);
      });
    }, 1000);
  };

  const runPredictionJob = async () => {
    try {
      const response = await fetch('/api/run-databricks-job', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' }
      });

      if (!response.ok) throw new Error('Failed to trigger job');
      await response.json();
      alert('Prediction job triggered successfully!');
    } catch (error) {
      console.error("Error triggering prediction job:", error);
      alert('Failed to trigger prediction job: ' + error.message);
    }
  };

  const sendMaintenanceAlert = async () => {
    const email = prompt('Enter email address for maintenance alert:');
    if (!email) return;

    try {
      const response = await fetch('/api/send-maintenance-alert', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ toEmail: email })
      });

      if (!response.ok) throw new Error('Failed to send email');
      await response.json();
      alert('Maintenance alert sent successfully!');
    } catch (error) {
      console.error("Error sending email:", error);
      alert('Failed to send email: ' + error.message);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Machine Error Code Frequencies</h2>
      <ResponsiveContainer width="100%" height={300}>
        <BarChart data={errorCodeData}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="errorCode" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Bar dataKey="count" fill="#8884d8" />
        </BarChart>
      </ResponsiveContainer>



      <h2>Average Runtime by Machine</h2>
      <ResponsiveContainer width="100%" height={300}>
        <BarChart data={machineRuntimeData}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="machineId" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Bar dataKey="avgRuntime" fill="#00C49F" />
        </BarChart>
      </ResponsiveContainer>

      <div style={{ marginTop: '30px' }}>
        <button onClick={refreshData} disabled={loading}>
          {loading ? "Refreshing..." : "Refresh Data"}
        </button>
        <button onClick={runPredictionJob} style={{ marginLeft: '10px' }}>
          Trigger Prediction Job
        </button>
        <button onClick={sendMaintenanceAlert} style={{ marginLeft: '10px' }}>
          Send Maintenance Alert
        </button>
      </div>
    </div>
  );
}
