import React, { useState, useEffect } from 'react';
import { Container, Typography, Button, Box, CircularProgress, Alert, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper } from '@mui/material';
import { Pie } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import Papa from 'papaparse';



ChartJS.register(ArcElement, Tooltip, Legend);

function Predictions() {
  const [predictions, setPredictions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [jobStatus, setJobStatus] = useState('');
  const [predictedFailures, setPredictedFailures] = useState(0); // Add state for predicted failures
  const [predictedFailuresData, setPredictedFailuresData] = useState([]); // Add state for pie chart data

  const API_URL = 'http://localhost:3001'; // Ensure this matches your backend

  useEffect(() => {
    fetchPredictions();
    fetchPredictedFailuresData();
  }, []);

  const fetchPredictions = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await fetch(`${API_URL}/api/latest-predictions`);
      if (!response.ok) throw new Error('Failed to fetch predictions');
      const result = await response.json();
      console.log('Predictions from backend:', result);
      setPredictions(Array.isArray(result.data) ? result.data : []);

      // Assuming your result contains a field for predicted failures in the next 16 hours
      const totalFailures = result.data.reduce((acc, prediction) => acc + (prediction.predictedFailure || 0), 0);
      setPredictedFailures(totalFailures); // Set predicted failures
    } catch (error) {
      console.error('Error:', error);
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const fetchPredictedFailuresData = async () => {
    try {
      const response = await fetch('https://mristorage1234.blob.core.windows.net/mri-logs/predicted_errors.csv/part-00000-tid-322964752325347736-d966b189-1320-4925-8ac7-6e33f3fb29a6-2360-1-c000.csv?sp=r&st=2025-04-19T12:54:31Z&se=2025-04-19T20:54:31Z&spr=https&sv=2024-11-04&sr=b&sig=QuVn35siITPC5pC099aQrhGCqxdx8SOV%2BZl0rx4sYBU%3D');
      if (!response.ok) throw new Error('Failed to fetch CSV from Blob Storage');

      const csvText = await response.text();

      Papa.parse(csvText, {
        header: true,
        skipEmptyLines: true,
        complete: (result) => {
          const data = result.data;
          const machineFailureCounts = {};

          const normalizeHeaders = (row) => {
            const normalized = {};
            for (let key in row) {
              normalized[key.trim()] = row[key];
            }
            return normalized;
          };

          data.forEach(raw => {
            const item = normalizeHeaders(raw);
            const machineId = item.machine_id?.trim();
            if (machineId) {
              machineFailureCounts[machineId] = (machineFailureCounts[machineId] || 0) + 1;
            }
          });

          const processedData = Object.entries(machineFailureCounts)
            .map(([machine, value]) => ({ name: machine, value }));

          if (processedData.length > 0) {
            setPredictedFailuresData(processedData);
          } else {
            setPredictedFailuresData([]);
          }
        },
        error: (error) => {
          console.error("Error parsing CSV:", error);
        }
      });
    } catch (error) {
      console.error("Error fetching predictions:", error);
    }
  };

  const triggerDatabricksJob = async () => {
    try {
      setJobStatus('Triggering Databricks job...');
      const response = await fetch(`${API_URL}/api/run-databricks-job`, {
        method: 'POST',
      });
      const result = await response.json();
      if (result.success) {
        setJobStatus(`✅ Job started with Run ID: ${result.run_id}`);
      } else {
        setJobStatus(`❌ Failed to trigger job`);
      }
    } catch (error) {
      console.error('Trigger Error:', error);
      setJobStatus(`❌ ${error.message}`);
    }
  };

  const handleRefresh = () => {
    setPredictions([]);
    setPredictedFailures(0);
    setJobStatus('');
    fetchPredictions(); // Fetch predictions again
  };

  const pieChartData = {
    labels: predictedFailuresData.map(item => item.name),
    datasets: [{
      data: predictedFailuresData.map(item => item.value),
      backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#FF9F40'],
    }]
  };

  return (
    <Container maxWidth="md" sx={{ textAlign: 'center', mt: 6 }}>
      <Typography variant="h4" gutterBottom>
        Databricks Predictions
      </Typography>

      <Button
        variant="contained"
        color="primary"
        onClick={triggerDatabricksJob}
        sx={{ mb: 2, padding: '10px 20px', fontSize: '16px' }}
      >
        ▶️ Run Prediction Model
      </Button>

      <Button
        variant="outlined"
        color="secondary"
        onClick={handleRefresh}
        sx={{ mb: 2, padding: '10px 20px', fontSize: '16px', ml: 2 }}
      >
        🔄 Refresh Predictions
      </Button>

      {jobStatus && (
        <Typography variant="body1" sx={{ mb: 2 }}>
          {jobStatus}
        </Typography>
      )}

      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          Error: {error}
        </Alert>
      )}

      {/* Display predicted number of failures */}
      <Typography variant="h6" sx={{ mt: 2 }}>
        Predicted number of machine failures in the upcoming 16 hours: 
      </Typography>

      {loading ? (
        <Box sx={{ display: 'flex', justifyContent: 'center' }}>
          <CircularProgress />
        </Box>
      ) : predictions.length === 0 ? (
        <Typography variant="body1" color="text.secondary" sx={{ mt: 2 }}>
          No prediction data available
        </Typography>
      ) : (
        <TableContainer component={Paper} sx={{ mt: 4 }}>
          <Table>
            <TableHead>
              <TableRow>
                {Object.keys(predictions[0]).map((key) => (
                  <TableCell key={key} align="center">
                    <Typography variant="h6" color="textPrimary">
                      {key.toUpperCase()}
                    </Typography>
                  </TableCell>
                ))}
              </TableRow>
            </TableHead>
            <TableBody>
              {predictions.map((row, index) => (
                <TableRow key={index}>
                  {Object.values(row).map((value, i) => (
                    <TableCell key={i} align="center">
                      {value}
                    </TableCell>
                  ))}
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      )}

      {/* Pie Chart */}
      {predictedFailuresData.length > 0 && (
        <Box sx={{ mt: 4 }}>
          <Typography variant="h6" sx={{ mb: 2 }}>
            Predicted Machine Failures (Pie Chart):
          </Typography>
          <Pie data={pieChartData} />
        </Box>
      )}
    </Container>
  );
}

export default Predictions;
