import React, { useState, useEffect } from 'react';

function S3Contents() {
  const [s3Contents, setS3Contents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const API_URL = 'http://localhost:5000';

  useEffect(() => {
    fetchS3Data();
  }, []);

  const fetchS3Data = async () => {
    try {
      setLoading(true);
      setError(null);

      const response = await fetch(`${API_URL}/api/s3-contents`, {
        method: 'GET',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to fetch S3 contents');
      }

      const data = await response.json();
      setS3Contents(data);
    } catch (error) {
      console.error('Error fetching S3 contents:', error);
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const retryFetch = () => {
    fetchS3Data();
  };

  return (
    <div className="page-container">
      <h1>S3 Bucket Contents</h1>
      {error && (
        <div className="error-message">
          Error: {error}
          <button onClick={retryFetch} className="retry-button">
            Retry
          </button>
        </div>
      )}
      {loading ? (
        <div className="loading">Loading...</div>
      ) : (
        <div className="content-section">
          <ul>
            {s3Contents.length === 0 ? (
              <li>No files found in S3 bucket</li>
            ) : (
              s3Contents.map((item, index) => (
                <li key={index}>{item.Key}</li>
              ))
            )}
          </ul>
        </div>
      )}
    </div>
  );
}

export default S3Contents;
