import React, { useState, useEffect } from 'react';

function S3Contents() {
  const [s3Contents, setS3Contents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchS3Contents();
  }, []);

  const fetchS3Contents = async () => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:3001/api/s3-contents');

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to fetch S3 contents');
      }

      const data = await response.json();
      setS3Contents(data);
    } catch (error) {
      console.error('Error:', error);
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleDownload = async (key) => {
    try {
      const downloadUrl = `https://${process.env.REACT_APP_S3_BUCKET}.s3.${process.env.REACT_APP_AWS_REGION}.amazonaws.com/${key}`;
      const link = document.createElement('a');
      link.href = downloadUrl;
      link.download = key.split('/').pop(); // get the filename from path
      link.click();
    } catch (err) {
      console.error('❌ Download failed:', err.message);
    }
  };

  return (
    <div className="page-container">
      <h1>S3 Bucket Contents</h1>
      {error && (
        <div className="error-message">
          Error: {error}
          <button onClick={fetchS3Contents} className="retry-button">Retry</button>
        </div>
      )}
      {loading ? (
        <div className="loading">Loading...</div>
      ) : (
        <div className="content-section">
          <ul>
            {s3Contents.length === 0 ? (
              <li>No files found in S3 bucket</li>
            ) : (
              s3Contents.map((item, index) => (
                <li key={index}>
                  {item.Key}
                  <button 
                    onClick={() => handleDownload(item.Key)} 
                    style={{ marginLeft: '10px' }}
                  >
                    ⬇ Download
                  </button>
                </li>
              ))
            )}
          </ul>
        </div>
      )}
    </div>
  );
}

export default S3Contents;
