import React, { useState } from 'react';
import { HashRouter as Router, Routes, Route, Link } from 'react-router-dom';

import S3Contents from './components/S3Contents';
import Predictions from './components/Predictions';
import AthenaQuery from './components/AthenaQuery';
import SendEmail from './components/SendEmail';


import './App.css';

function App() {
  const [streaming, setStreaming] = useState(false);
  const [streamMessage, setStreamMessage] = useState('');

  const toggleStream = async () => {
    try {
      const res = await fetch('http://localhost:3001/api/toggle-stream', {
        method: 'POST',
      });
      const data = await res.json();
      setStreaming(data.streaming);
      setStreamMessage(data.message || '');
    } catch (error) {
      console.error('Error toggling stream:', error);
      setStreamMessage('‚ùå Failed to toggle stream.');
    }
  };

  return (
    <Router>
      <div className="App">
        <nav className="nav-bar">
          <ul>
            <li><Link to="/">Home</Link></li>
            <li><Link to="/s3">S3 Contents</Link></li>
            <li><Link to="/predictions">Predictions</Link></li>
            <li><Link to="/athena">Athena Query</Link></li>
            <li><Link to="/send-email">Send Email</Link></li>

          </ul>
        </nav>

        <main>
          <Routes>
            <Route path="/" element={
              <div className="home-page">
                <h1>Cloud Project Dashboard</h1>
                <p>Select a section from the navigation menu above</p>

                <button onClick={toggleStream}>
                  {streaming ? 'üõë Stop Data Generation' : '‚ñ∂Ô∏è Start Data Generation'}
                </button>
                {streamMessage && <p style={{ marginTop: '10px' }}>{streamMessage}</p>}
              </div>
            } />
            <Route path="/s3" element={<S3Contents />} />
            <Route path="/predictions" element={<Predictions />} />
            <Route path="/athena" element={<AthenaQuery />} />
            <Route path="/send-email" element={<SendEmail />} />

          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
